CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE TABLE IF NOT EXISTS "education_streams" ("id" integer primary key autoincrement not null, "key" varchar not null, "name_hindi" varchar not null, "name_english" varchar not null, "description_hindi" text not null, "description_english" text not null, "subjects_hindi" text, "subjects_english" text, "career_paths_hindi" text, "career_paths_english" text, "icon" varchar, "color" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "education_streams_key_unique" on "education_streams" ("key");
CREATE TABLE IF NOT EXISTS "education_sectors" ("id" integer primary key autoincrement not null, "key" varchar not null, "name_hindi" varchar not null, "name_english" varchar not null, "description_hindi" text not null, "description_english" text not null, "why_important_hindi" text, "why_important_english" text, "eligibility_10th_hindi" text, "eligibility_10th_english" text, "eligibility_12th_hindi" text, "eligibility_12th_english" text, "career_prospects_hindi" text, "career_prospects_english" text, "icon" varchar, "color" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "education_sectors_key_unique" on "education_sectors" ("key");
CREATE TABLE IF NOT EXISTS "courses" ("id" integer primary key autoincrement not null, "sector_id" integer not null, "stream_id" integer, "key" varchar not null, "name_hindi" varchar not null, "name_english" varchar not null, "description_hindi" text not null, "description_english" text not null, "duration" varchar not null, "eligibility_hindi" text not null, "eligibility_english" text not null, "job_prospects_hindi" text, "job_prospects_english" text, "avg_salary_hindi" text, "avg_salary_english" text, "required_subjects_hindi" text, "required_subjects_english" text, "skills_needed_hindi" text, "skills_needed_english" text, "course_type" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("sector_id") references "education_sectors"("id") on delete cascade, foreign key("stream_id") references "education_streams"("id") on delete set null);
CREATE UNIQUE INDEX "courses_key_unique" on "courses" ("key");
CREATE TABLE IF NOT EXISTS "competitive_exams" ("id" integer primary key autoincrement not null, "sector_id" integer not null, "stream_id" integer, "key" varchar not null, "name_hindi" varchar not null, "name_english" varchar not null, "sector" varchar not null, "applicable_for" varchar not null, "eligibility_class" varchar not null, "eligibility_hindi" text, "eligibility_english" text, "exam_pattern" varchar, "frequency" varchar, "subjects_hindi" text, "subjects_english" text, "preparation_tips_hindi" text, "preparation_tips_english" text, "official_website" varchar, "registration_period" varchar, "exam_months" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("sector_id") references "education_sectors"("id") on delete cascade, foreign key("stream_id") references "education_streams"("id") on delete set null);
CREATE UNIQUE INDEX "competitive_exams_key_unique" on "competitive_exams" ("key");
CREATE TABLE IF NOT EXISTS "user_education_profiles" ("id" integer primary key autoincrement not null, "user_id" integer not null, "current_class" varchar not null, "planned_stream" varchar, "interest_tags" text, "strengths_hindi" text, "strengths_english" text, "challenges_hindi" text, "challenges_english" text, "family_support_level" varchar, "financial_constraints" varchar, "career_goals_hindi" text, "career_goals_english" text, "preferred_learning_style" text, "profile_completed_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "samarth_users"("id") on delete cascade);
CREATE UNIQUE INDEX "user_education_profiles_user_id_unique" on "user_education_profiles" ("user_id");
CREATE TABLE IF NOT EXISTS "user_education_plans" ("id" integer primary key autoincrement not null, "user_id" integer not null, "profile_id" integer not null, "plan_type" varchar not null, "recommended_streams" text, "recommended_sectors" text, "recommended_courses" text, "recommended_exams" text, "plan_data" text, "progress" text, "milestones" text, "study_schedule" text, "resources" text, "personalized_message_hindi" text, "personalized_message_english" text, "is_active" tinyint(1) not null default '1', "generated_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "samarth_users"("id") on delete cascade, foreign key("profile_id") references "user_education_profiles"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime);
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE TABLE IF NOT EXISTS "samarth_users" ("id" integer primary key autoincrement not null, "username" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "first_name" varchar, "last_name" varchar, "gender" varchar check ("gender" in ('male', 'female', 'other')), "date_of_birth" date, "phone" varchar, "address" text, "city" varchar, "state" varchar, "country" varchar, "postal_code" varchar, "role" varchar check ("role" in ('user', 'admin', 'moderator')) not null default 'user', "status" varchar check ("status" in ('active', 'inactive', 'suspended')) not null default 'active', "language" varchar not null default 'hindi', "preferences" text, "is_terms_accepted" tinyint(1) not null default '0', "is_privacy_accepted" tinyint(1) not null default '0', "timezone" varchar, "two_factor_enabled" tinyint(1) not null default '0', "last_login_at" datetime, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "security_question" varchar not null default 'school', "security_answer" varchar);
CREATE UNIQUE INDEX "samarth_users_username_unique" on "samarth_users" ("username");
CREATE UNIQUE INDEX "samarth_users_email_unique" on "samarth_users" ("email");
CREATE TABLE IF NOT EXISTS "user_profiles" ("id" integer primary key autoincrement not null, "user_id" integer not null, "bio" text, "location" varchar, "website" varchar, "avatar" varchar, "preferred_language" varchar not null default 'hindi', "created_at" datetime, "updated_at" datetime, "display_name" varchar, "language_preference" varchar not null default 'hi', "notification_preferences" text, foreign key("user_id") references "samarth_users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "goals" ("id" integer primary key autoincrement not null, "title_hindi" varchar not null, "title_english" varchar, "description_hindi" text, "description_english" text, "category" varchar, "target_age_group" varchar, "estimated_time_hours" integer not null default '1', "points_reward" integer not null default '10', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "challenges" ("id" integer primary key autoincrement not null, "title_hindi" varchar not null, "title_english" varchar, "description_hindi" text, "description_english" text, "category" varchar, "estimated_time_minutes" integer not null default '30', "points_reward" integer not null default '5', "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "user_progress" ("id" integer primary key autoincrement not null, "user_id" integer not null, "goal_id" integer not null, "progress_percentage" numeric not null default '0', "status" varchar not null default 'in_progress', "started_at" datetime, "completed_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "samarth_users"("id") on delete cascade, foreign key("goal_id") references "goals"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "user_challenges" ("id" integer primary key autoincrement not null, "user_id" integer not null, "challenge_id" integer not null, "is_completed" tinyint(1) not null default '0', "completed_at" datetime, "points_earned" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "samarth_users"("id") on delete cascade, foreign key("challenge_id") references "challenges"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "videos" ("id" integer primary key autoincrement not null, "name" varchar not null, "link" varchar not null, "type" varchar not null default 'motivational', "thumbnail" varchar, "duration" varchar, "views_count" integer not null default '0', "likes_count" integer not null default '0', "comments_count" integer not null default '0', "author" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "video_comments" ("id" integer primary key autoincrement not null, "video_id" integer not null, "user_id" integer not null, "comment" text not null, "created_at" datetime, "updated_at" datetime, foreign key("video_id") references "videos"("id") on delete cascade, foreign key("user_id") references "samarth_users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "video_likes" ("id" integer primary key autoincrement not null, "video_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("video_id") references "videos"("id") on delete cascade, foreign key("user_id") references "samarth_users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "video_shares" ("id" integer primary key autoincrement not null, "video_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("video_id") references "videos"("id") on delete cascade, foreign key("user_id") references "samarth_users"("id") on delete cascade);
